const API_KEY =
  "eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI5NzA5NjgyYWJiNDUwODk4ZmE5ODdkOTI5MWE2YzljMiIsInN1YiI6IjY1ZTY2ODZjZGM4NjQ3MDE4NzRmMjNlYSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.q5MqfgBQmlE4QS8gNh_4ivhadhydQ3MExoqICHuJd8c";
const BASE_URL = "https://api.themoviedb.org/3";

export function getMovies() {
  return fetch(`${BASE_URL}/movie/now_plying?api_key=${API_KEY}`).then(
    (response) => response.json()
  );
}
